import streamlit as st

# Configuration de la page
st.set_page_config(page_title="Album de mon chat", layout="wide")

# Initialisation de la session
if "authenticated" not in st.session_state:
    st.session_state["authenticated"] = False

# Fonction pour la page d'authentification
def login():
    st.title("Connexion")
    username = st.text_input("Nom d'utilisateur")
    password = st.text_input("Mot de passe", type="password")
    if st.button("Se connecter"):
        if username == "root" and password == "password":  # Changez le mot de passe ici
            st.session_state["authenticated"] = True
            st.success("Connexion r√©ussie !")
        else:
            st.error("Nom d'utilisateur ou mot de passe incorrect")

# Fonction pour afficher la page d'accueil
def home():
    st.sidebar.title("Bienvenue root")
    st.sidebar.markdown("### üòª Accueil")
    st.sidebar.markdown("### üê± Les photos de mon chat", unsafe_allow_html=True)

    st.sidebar.button("D√©connexion", on_click=logout)

    st.markdown(
        "<h1 style='text-align: center;'>Bienvenue dans l'album de mon chat üê±</h1>",
        unsafe_allow_html=True,
    )
    st.write("Ceci est la page d'accueil. Cliquez sur 'Les photos de mon chat' pour voir l'album.")

# Fonction pour afficher l'album de photos
def album():
    st.sidebar.title("Bienvenue root")
    st.sidebar.markdown("### üòª Accueil")
    st.sidebar.markdown("### üê± Les photos de mon chat", unsafe_allow_html=True)

    st.sidebar.button("D√©connexion", on_click=logout)

    st.markdown(
        "<h1 style='text-align: center;'>Bienvenue dans l'album de mon chat üê±</h1>",
        unsafe_allow_html=True,
    )

    # Affichage des images
    col1, col2, col3 = st.columns(3)

    with col1:
        st.image("https://static.vecteezy.com/ti/vecteur-libre/p1/17047854-illustration-de-chat-mignon-chat-kawaii-chibi-style-de-dessin-dessin-anime-de-chat-vectoriel.jpg", caption="Chat grincheux", use_column_width=True)
    with col2:
        st.image("data:image/png;base64,", caption="Chacha", use_column_width=True)
    with col3:
        st.image("data:image/png;base64,", caption="Chat prichti", use_column_width=True)

# Fonction pour la d√©connexion
def logout():
    st.session_state["authenticated"] = False
    st.experimental_rerun()

# Navigation
if not st.session_state["authenticated"]:
    login()
else:
    menu = st.sidebar.radio("Navigation", ["Accueil", "Les photos de mon chat"])
    if menu == "Accueil":
        home()
    elif menu == "Les photos de mon chat":
        album()
